mapper.update:
UPDATE btf_emp_info
id = #{id,jdbcType=VARCHAR}, emp_code = #{emp_code,jdbcType=VARCHAR}, org_code = #{org_code,jdbcType=VARCHAR}, emp_type = #{emp_type,jdbcType=VARCHAR}, emp_status = #{emp_status,jdbcType=VARCHAR}, emp_name = #{emp_name,jdbcType=VARCHAR}, emp_logo = #{emp_logo,jdbcType=VARCHAR}, emp_ename = #{emp_ename,jdbcType=VARCHAR}, party = #{party,jdbcType=VARCHAR}, gender = #{gender,jdbcType=VARCHAR}, birthdate = #{birthdate,jdbcType=VARCHAR}, position = #{position,jdbcType=VARCHAR}, card_type = #{card_type,jdbcType=VARCHAR}, card_no = #{card_no,jdbcType=VARCHAR}, in_date = #{in_date,jdbcType=VARCHAR}, out_date = #{out_date,jdbcType=VARCHAR}, o_tel = #{o_tel,jdbcType=VARCHAR}, o_address = #{o_address,jdbcType=VARCHAR}, o_zipcode = #{o_zipcode,jdbcType=VARCHAR}, o_email = #{o_email,jdbcType=VARCHAR}, o_fzxno = #{o_fzxno,jdbcType=VARCHAR}, h_tel = #{h_tel,jdbcType=VARCHAR}, h_address = #{h_address,jdbcType=VARCHAR}, h_zipcode = #{h_zipcode,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, qq_no = #{qq_no,jdbcType=VARCHAR}, msn = #{msn,jdbcType=VARCHAR}, emp_desc = #{emp_desc,jdbcType=VARCHAR}, work_exp = #{work_exp,jdbcType=VARCHAR}, remark = #{remark,jdbcType=VARCHAR},
WHERE 1=1 and id = #{id}

mapper.select:
    	
    	select  from
   			(select a.*,rownum row_num from
      			(
      			select  from btf_emp_info 
        			${orderBy}
        			LIMIT ${offset}, ${length}
        		) a
   			) b 
		where b.row_num between ${offset} and ${length}
