mapper.update:
UPDATE btf_org_info
id = #{id,jdbcType=VARCHAR}, org_code = #{org_code,jdbcType=VARCHAR}, org_name = #{org_name,jdbcType=VARCHAR}, org_ename = #{org_ename,jdbcType=VARCHAR}, org_type = #{org_type,jdbcType=VARCHAR}, org_degree = #{org_degree,jdbcType=VARCHAR}, org_status = #{org_status,jdbcType=VARCHAR}, org_work_status = #{org_work_status,jdbcType=VARCHAR}, logout_time = #{logout_time,jdbcType=TIMESTAMP}, login_time = #{login_time,jdbcType=TIMESTAMP}, valid_flag = #{valid_flag,jdbcType=VARCHAR}, par_org_code = #{par_org_code,jdbcType=VARCHAR}, org_seq = #{org_seq,jdbcType=VARCHAR}, org_addr = #{org_addr,jdbcType=VARCHAR}, org_zipcode = #{org_zipcode,jdbcType=VARCHAR}, org_email = #{org_email,jdbcType=VARCHAR}, org_linkman = #{org_linkman,jdbcType=VARCHAR}, org_linktel = #{org_linktel,jdbcType=VARCHAR}, LINKMAN = #{LINKMAN,jdbcType=VARCHAR}, ORG_MANAGER = #{ORG_MANAGER,jdbcType=VARCHAR}, auth_user_list = #{auth_user_list,jdbcType=VARCHAR}, START_DATE = #{START_DATE,jdbcType=TIMESTAMP}, END_DATE = #{END_DATE,jdbcType=TIMESTAMP}, AREA = #{AREA,jdbcType=VARCHAR}, OPEN_TIME = #{OPEN_TIME,jdbcType=VARCHAR}, CLOSE_TIME = #{CLOSE_TIME,jdbcType=VARCHAR}, MARK = #{MARK,jdbcType=VARCHAR}, PEN_DATE = #{PEN_DATE,jdbcType=TIMESTAMP}, ORG_ACCT_DATE = #{ORG_ACCT_DATE,jdbcType=TIMESTAMP}, OP_FLAG = #{OP_FLAG,jdbcType=VARCHAR}, TD_FLAG = #{TD_FLAG,jdbcType=VARCHAR}, FOR_TYPE = #{FOR_TYPE,jdbcType=VARCHAR}, CASH_USER_CNT = #{CASH_USER_CNT,jdbcType=INTEGER}, MCCTL_USER_CNT = #{MCCTL_USER_CNT,jdbcType=INTEGER}, SIGN_OUT_FLAG = #{SIGN_OUT_FLAG,jdbcType=VARCHAR},
WHERE 1=1 and id = #{id}

mapper.select:
    	
    	select  from
   			(select a.*,rownum row_num from
      			(
      			select  from btf_org_info 
        			${orderBy}
        			LIMIT ${offset}, ${length}
        		) a
   			) b 
		where b.row_num between ${offset} and ${length}
